/**
 * Library Effects API - Fetch user's generated effects
 */

import { NextResponse } from 'next/server';
import { auth } from '@clerk/nextjs/server';
import { supabase } from '@/lib/supabase';
import { corsResponse, handleOptions } from '@/lib/cors';

export async function OPTIONS() {
  return handleOptions();
}

export async function GET() {
  try {
    const { userId } = await auth();
    if (!userId) {
      return corsResponse(NextResponse.json({ error: 'Unauthorized' }, { status: 401 }));
    }

    // Fetch effects generated by this user from combined_media table
    const { data, error } = await supabase
      .from('combined_media')
      .select('id, title, audio_url, image_url, created_at, plays, metadata, prompt')
      .eq('user_id', userId)
      .eq('genre', 'effects') // Effects are stored with genre='effects'
      .order('created_at', { ascending: false });

    if (error) {
      console.error('Supabase error fetching effects:', error);
      return corsResponse(NextResponse.json({ error: 'Failed to fetch effects' }, { status: 500 }));
    }

    return corsResponse(NextResponse.json({
      success: true,
      effects: data || [],
    }));
  } catch (error) {
    console.error('Error in /api/library/effects:', error);
    return corsResponse(NextResponse.json({ error: 'Internal server error' }, { status: 500 }));
  }
}
